import React, { Component } from "react";
import "./App.css";
import ProfileContainer from "./containers/ProfileContainer";

const fetchURL = "http://10.198.66.254:3000";
const fetchUsers = fetchURL + "/users";

class App extends Component {
  state = {
    allUsers: [],
    currentUserId: 1, // update this to backend
    currentUser: {}
  };

  componentDidMount() {
    this.fetchUsers();
  }

  fetchUsers = () => {
    fetch(fetchUsers)
      .then(res => res.json())
      .then(users => this.setState({ allUsers: users }));
  };

  findCurrentUser = userId => {
    const currentUser = this.state.allUsers.find(user => user.id === userId)
  };

  render() {
    const { allUsers, currentUserId } = this.state;
