import React, { Component } from "react";
import { Dropdown } from "semantic-ui-react";

const categories = [
  {
    key: "sushi",
    text: "Sushi",
    value: "sushi"
  },
  {
    text: "American",
    value: "american"
  },
  {
    text: "Burgers",
    value: "burgers"
  },
  {
    text: "Italian",
    value: "italian"
  },
  {
    text: "Greek",
    value: "greek"
  },
  {
    text: "Chinese",
    value: "chinese"
  },
  {
    text: "Mexican",
    value: "mexican"
  },
  {
    text: "Asian",
    value: "asian"
  },
  {
    text: "Salads",
    value: "salads"
  }
];

const cityOptions = [
  { key: "chicago", value: "chicago", text: "Chicago" },
  { key: "new york", value: "new york", text: "New York" },
  { key: "detroit", value: "detroit", text: "Detroit" }
];

export default class RouletteForm extends Component {

  // state = {
