  state = {
    allUsers: [],
    currentUserId: 1 // update this to backend
  };

  componentDidMount() {
    this.fetchUsers();
  }

  fetchUsers = () => {
    fetch(fetchUsers)
      .then(res => res.json())
      .then(users => this.setState({ allUsers: users }));
