    console.log(currentUser)

    console.log(this.state);
    return (
      <div>
        <ProfileContainer currentUser={currentUser}/>
        <RouletteContainer />
        <TeamContainer allUsers={this.state.allUsers}/>
      </div>
    );
  }
}

export default App;
