import { Button, Form, Header, Image } from "semantic-ui-react";

export default class TeamForm extends Component {
  state = {
    teamNameInput: ''
  }

  handleChange = e => {
    const target = e.target.value
    console.log(target)
    this.setState({teamNameInput: target})
  }
  render() {
    const { currentUser } = this.props
    const { teamNameInput } = this.state
    // currentUser ? console.log(currentUser.city) : null
    // console.log(teamNameInput)
    return (
      <div>
        <Form>
          <Form.Field widths="equal">
            <label>Team Name</label>
            <input onChange={this.handleChange} placeholder="Enter a team name..." />
          </Form.Field>
          <Button type="submit">Create Team</Button>
        </Form>
      </div>
    );
  }
}
