import React, { Component } from "react";
import { Button, Form, Search } from "semantic-ui-react";
import _ from "lodash";

export default class MemberForm extends Component {
  // state = {
  //   searchInput: ""
  // };

  handleChange = (e, value ) => {
    this.setState({searchInput: value})
  };

  render() {
    const { addMember, searchInput } = this.props
    // const { searchInput } = this.state
    // console.log(this.state)
    return (
      <div>
        <Form onSubmit={(event) => addMember(event, searchInput)}>
          <Form.Field widths="equal">
            <label>Team Members</label>
            {/* <label>{currentUser ? currentUser.username : null}</label> */}
            {/* <input
              type="text"
              name="searchInput"
              placeholder="Add new member here..."
            /> */}
            <Search
              onSearchChange={_.debounce(
                (event, { value }) => this.handleChange(event, value),
                500
              )}
              showNoResults={false}
            />
            {/* <Button type="submit">Add Team Member</Button> */}
          </Form.Field>
        </Form>
      </div>
    );
  }
}
