    const currentUser = allUsers.filter(user => Number(user.id) === currentUserId)
    // const currentTeam = allTeams.filter(team => Number(team.id) === currentTeamId)
    console.log(currentUser)
    // console.log(currentTeam)
    return (
      <div>
        {/* <ProfileContainer currentUser={currentUser}/> */}
        <TeamContainer allUsers={allUsers} allTeams={allTeams} currentUser={currentUser} fetchURL={fetchURL}/>
        <RouletteContainer />
      </div>
    );
  }
}

export default App;
