import React, { Component } from 'react'
import TeamForm from './TeamForm'
import TeamSelection from './TeamSelection'
import MemberForm from './MemberForm'

export default class TeamContainer extends Component {

    state = {
        // unselected: [],
        // selected: []
        team_name: '',
        location: '',
        members: []
    }

    updateTeam = (id) => {
        let unselected = this.state.unselected.filter(person => person.id !== id)
        let selected = this.state.unselected.filter(person => person.id === id)
        this.setState({unselected})
        this.setState({selected})
    }

    addATeam = (e, team) => {
        // console.log(team)
        this.setState({
            team_name: team.teamNameInput,
            location: team.locationInput,
        })
    }

    addMember = (e, member) => {
        this.setState({members: [...this.state.members, member]})
    }

    render() {
        const {allUsers, currentUser} = this.props
        console.log(this.state)
        return (
            <div>
                <TeamForm currentUser={currentUser} addATeam={this.addATeam}/>
                <MemberForm addMember={this.addMember} />
                <TeamSelection allUsers={allUsers}/>

            </div>
        )
    }
}
