import React, { Component } from "react"
import { Card, Icon, Image, Button } from "semantic-ui-react"

export default class RouletteCard extends Component {
  state = {
    indexArray: [0]
  }

  randomYelp = array => {
    let randIndex = Math.floor(Math.random() * 10)
    while (array.includes(randIndex)) {
      // console.log(randIndex)
      randIndex = Math.floor(Math.random() * 10)
    }
    // array.push(randIndex)
    this.setState({ indexArray: [...this.state.indexArray, randIndex] })
    // console.log(array)
    // return randIndex
  }

  render() {
    const { yelpResults } = this.props
    const { indexArray } = this.state
    let result = yelpResults[indexArray[indexArray.length - 1]]
    console.log(yelpResults)
    console.log(result)
    const {
    //   image_url,
      name,
      rating,
      location: { display_address },
      price,
      is_closed,
      display_phone
    } = result
    return (
      <div className="roulette-card">
        <Card>
          <Image
            src="https://react.semantic-ui.com/images/avatar/large/matthew.png"
            wrapped
            ui={false}
          />
          <Card.Content>
            <Card.Header>Restaurant Name</Card.Header>
            <Card.Description>Restaurant Descp</Card.Description>
          </Card.Content>
          <Button onClick={() => this.randomYelp(indexArray)} primary>
            Spin Again
          </Button>
          <Button primary>OK</Button>
        </Card>

        {/* Restaurant Address  */}
        {/* Restaurant Image  */}
        {/* Restaurant Category */}
        {/* Restaurant Price Range */}
        {/* <button>Spin Again</button> 
                <button>OK</button> */}
        {/* Counter at the bottom to say user has X more spins */}
        {/* ROUTE TO ROULETTE CARD */}
      </div>
    )
  }
}
