  state = {
    location: "",
    category: ""
  };

  handleSubmit = () => {
    this.setState({})
  }


  render() {
    return (
      <div>
        <RouletteForm handleDropDown={this.handleDropdown}/>
        <RouletteCard yelpResults={this.props.yelpResults}/> 
        <SpinButton />

      </div>
    );
  }
}
